int a;
float mynumber;
int a, b, c;

int a=100;
float b=10.50;
char c='a';

// Write a C++ program to illustrate variable declaration and initialization.
#include<iostram>
using namespace std;
int main ()
    {
    int a=5; //initial value = 5
    int b(2); // initial value = 2
    int result; // initial value undetermined
    a = a + 3;
    result = a - b;
    cout << result;
    return 0;
}

//Write a C++ program to gets the values for two numbers from the keyboard and adds them together and prints the result on the monitor.
#include<iostream>
using namespace std;
int main ()
    {
    // Definition
    int num1;
    int num2;
    int sum;
    // Getting inputs
    cout << "Enter the first number: "; cin >> num1;
    cout << "Enter the second number: "; cin >> num2;
    // Calculation and storing result
    sum = num1 + num2;
    // Display output
    cout << "The sum is: " << sum;
    return 0;
    }

