//Write a  C++ program to demonstrate the use of parameterized constructor for string data.
#include <iostream>
#include<string.h>
using namespace std;
class strings
    {
    char str[20];
    public :
    strings( );
    strings (char s[ ]);
    void show( );
    };
strings :: strings( )
    {
    strcpy (str,"Hari");
    cout<<"Default constructor called\n";
    }
strings :: strings (char s[ ])
    {
    strcpy(str,s);
    cout<<"One argument constructor called\n";
    }
void strings :: show( )
    {
    cout<<"string is"<<str<<endl;
    }
int main( )
    {
    strings s1;
    s1.show( );
    s1=strings("Vijay");
    s1.show( );
    strings s2=strings("Manmohan");
    s2.show( );
    strings s3("Ranjana");
    s3.show( );
    }

