//Write a C++ program to demonstrate the use of copy constructor .
#include<iostream>
using namespace std;
class demo
    {
    int data;
    public :
    demo( )
        {
        data =200;
        cout<<"Default constructor is called\n";
        }
    demo(int x)
        {
        data = x;
        }
    demo (demo &d)
        {
        data= d.data;
        cout<<"copy constructor is called\n";
        }
    void show( )
        {
        cout<<"data="<<data<<endl;
        }
    };
int main( )
    {
    demo d1(300);
    demo d2=d1;
    demo d3;
    d3=d2;
    demo d4=d3;
    d1.show( );
    d2.show( );
    d3.show( );
    d4.show( );
    }

//Write a C++ program to demonstrate the use of copy constructor .
#include<iostream>
using namespace std;
class code
    {
    int id;
    public:
    code() { } // constructor
    code(int a)
        {
        id = a; // constructor again
        }
    code(code & x) // copy constructor
        {
        id = x.id; // copy in the value
        }
    void display(void)
        {
        cout << id;
        }
    };
int main()
    {
    code A(100); // object A is created and initialized
    code B(A); // copy constructor called
    code C = A; // copy constructor called again
    code D; // D is created, not initialized
    D = A; // copy constructor not called
    cout << "\n id of A:";
    A.display();
    cout << "\n id of B:";
    B.display();
    cout << "\n id of C:";
    C.display();
    cout << "\n id of D:";
    D.display();
    return 0;
    }

//Write a C++ program to demonstrate the constructors that are used to construct strings in objects.
#include<iostream>
#include<string.h>
using namespace std;
class String
    {
    char *name;
    int length;
    public:
    String() // constructor-1
        {
        length = 0;
        name = new char[length + 1];
        }
    String(char *s) // constructor-2
        {
        length = strlen(s);
        name = new char[length + 1]; // one additional character for \0
        strcpy(name, s);
        }
    void display(void)
        {
        cout << name << "\n";
        }
    void join(String &a, String &b);
    };
void String :: join(String &a, String &b)
    {
    length = a.length + b.length;
    delete name;
    name = new char[length+1]; // dynamic allocation
    strcpy(name, a.name);
    strcat(name, b.name);
    };
int main()
    {
    char *first = "Joseph ";
    String name1(first), name2("Louis "), name3 ("Lagrange"), s1,s2;
    s1.join(name1, name2);
    s2.join(s1, name3);
    name1.display();
    name2.display();
    name3.display();
    s1.display();
    s2.display();
    return 0;
    }

