//Write a C++ program to illustrate virtual function.
#include<iostream>
using namespace std;
class first
    {
    public :
    virtual void show( )
        {
        cout<<"Hello from show of first class"<<endl;
        }
    };
class second :public first
    {
    public :
    void show( )
        {
        cout<<"Hello from show of second class"<<endl;
        }
    };
int main( )
    {
    first *ptr;
    first f;
    second s;
    ptr =&f;
    ptr->show( );
    ptr =&s;
    ptr->show( );
    }

//Write a C++ program to illustrate another example of virtual function.
#include<iostream>
using namespace std;
class first
    {
    public :
    virtual void show( )
        {
        cout<<"Hello from show of first class"<<endl;
        }
    void display( )
        {
        cout<<"Hello from display of first class"<<endl;
        }
    };
class second :public first
    {
    public :
    void show( )
        {
        cout<<"Hello from show of second class"<<endl;
        }
    void display( )
        {
        cout<<"Hello from display of second class"<<endl;
        }
    };
int main( )
    {
    first *ptr;
    first f;
    second s;
    ptr=&f;
    ptr->show( );
    ptr->display( );
    ptr=&s;
    ptr->show( );
    ptr->display( );
    }

//Write a C++ program to illustrate another example of virtual function.
#include<iostream>
using namespace std;
class first
    {
    public :
    void show( )
    {
    cout<<"Hello from show of first class"<<endl;
    }
    };
class second : public first
    {
    public :
    virtual void show( )
    {
    cout<<"Hello from show of second class"<<endl;
    }
    };
class third :public second
    {
    public :
    void show( )
        {
        cout<<"Hello from show of third class"<<endl;
        }
    };
int main( )
    {
    first *fptr;
    first f;
    second s, *sptr;
    third t;
    fptr=&f;
    fptr->show( );
    fptr=&s;
    fptr->show( );
    sptr=&s;
    sptr->show( );
    sptr=&t;
    sptr->show( );
    }

