//Write a C++ program to illustrate operator overloading, adding integer to object.
#include<iostream>
using namespace std;
class demo_op1
    {
    private :
    int num;
    public:
    void input( )
        {
        cout<<"Enter the number"<<endl;
        cin>> num;
        }
    void operator +(int x)
        {
        num=num+x;
        }
    void show( )
        {
        cout<<"The num is "<<num<<endl;
        }
    };
int main( )
    {
    demo_op1 d1;
    d1.input( );
    d1.show( );
    int x;
    cout<<"Enter the value you want to add\n";
    cin>>x;
    d1+x;
    d1.show( );
    }

//Write a C++ program to illustrate operator overloading + with class object as argument.
#include<iostream>
using namespace std;
class demo_sum
    {
    private :
    int num;
    static int count;
    public :
    void input( )
        {
        cout<<"Enter the number for object"<<++count<<"\n";
        cin>>num;
        }
    int operator + (demo_sum temp)
        {
        return(num + temp.num);
        }
    void show( )
        {
        cout<<"The num is "<<num<<endl;
        }
    };
int demo_sum ::count;
int main( )
    {
    demo_sum d1, d2;
    d1.input( );
    d2.input( );
    d1.show( );
    d2.show( );
    int sum=d1+d2;
    cout<<"The sum of two object’s num is "<<sum<<endl;
    }

//Write a C++ program to illustrate operator overloading +, -, *, AND / in the same program.
#include<iostream>
using namespace std;
class demo_exp
    {
    private :
    float num;
    static int count1, count2;
    public :
    void input( )
        {
        cout<<"Enter the number for object"<<++count1<<endl;
        cin>>num;
        }
    demo_exp operator *(demo_exp temp)
        {
        demo_exp t;
        t.num=num*temp.num;
        return t;
        }
    demo_exp operator +(demo_exp temp)
        {
        demo_exp t;
        t.num=num+temp.num;
        return t;
        }
    demo_exp operator -(demo_exp temp)
        {
        demo_exp t;
        t.num=num-temp.num;
        return t;
        }
    demo_exp operator /(demo_exp temp)
        {
        demo_exp t;
        if(temp.num)
            t.num=num/temp.num;
        else
            {
            cout<<"Division by zero is not allowed"<<endl;
            exit(0);
            }
        return t;
        }
    void show( )
        {
        cout<<"The sum for object"<<++count2<<"is "<<num<<endl;
        }
    };
int demo_exp :: count1;
int demo_exp :: count2;
int main( )
    {
    demo_exp d1,d2,d3,d4,d5;
    d1.input( );
    d2.input( );
    d3.input( );
    d4.input( );
    d1.show( );
    d2.show( );
    d3.show( );
    d4.show( );
    d5=d1+d2*d3/d1-d4;
    d5.show( );
    }

//Write a C++ program to illustrate operator overloading for > operator.
#include<iostream>
using namespace std;
class emp
    {
    private :
