    e2.show( );
    e1>e2;
    }

//Write a C++ program to illustrate operator overloading for = operator.
#include<iostream>
using namespace std;
class demo
    {
    int num;
    public :
    demo(int n)
        {
        num=n;
        }
    demo( ) {}
    void operator =(demo temp)
        {
        num=temp.num;
        cout<<"hello from ="<<endl;
        }
    void show( )
        {
        cout<<"num="<<num<<endl;
        }
    };
int main( )
    {
    demo d1(20);
    demo d2=d1;
    d1.show( );
    d2.show( );
    }

//Write a C++ program to illustrate operator overloading for ++ unary operator.
#include<iostream>
using namespace std;
class demo
    {
    int num;
    public :
    demo( ) {}
    demo (int x)
        {
        num=x;
        }
    demo operator++(int)
        {
        demo temp;
        temp.num=num;
        num++;
        return temp;
        }
    void show(char *s)
        {
        cout<<"num of object"<<s<<"="<<num<<endl;
        }
    };
int main( )
    {
    demo d1(20),d2;
    d2=d1++;
    d1.show ("d1");
    d2.show ("d2");
    }

//Write a C++ program to illustrate operator overloading for pre ++ and post ++ unary operator in the same program.
#include<iostream>
using namespace std;
class demo
    {
    int num;
    public :
    demo( ) {} demo (int x)
        {
        num = x;
        }
    demo operator ++(int)
        {
        demo temp;
        temp.num = num;
        num++;
        return temp;
        }
    demo operator++( )
        {
        demo temp;
        num++;
        temp.num = num;
        return temp;
        }
    void show(char*s)
        {
        cout<<"NUM OF OBJECT"<<s<<" = "<<num<<endl;
        }
    };
int main( )
    {
    demo d1(30),d2,d3;
    d2=d1++;
    d3=++d1;
    d1.show("d1");
    d2.show("d2");
    d3.show("d3");
    }

