//Write a C++ program to illustrate operator overloading + operator.
#include<iostream>
using namespace std;
class demo
    {
    int num;
    public :
    demo(int x)
        {
        num=x;
        }
    void operator +( )
        {
        num=num>0 ?num :-num;
        }
    void show( )
        {
        cout<<"num = "<<num<<endl;
        }
    };
int main( )
    {
    demo d1(-100);
    cout<<"Before"<<endl;
    d1.show( );
    +d1;
    cout<<"After"<<endl;
    d1.show( );
    }

//Write a C++ program to illustrate operator overloading - operator using friend function.
#include<iostream>
using namespace std;
class demo
    {
    int num;
    public :
    demo( ) {}
    demo(int x)
        {
        num=x;
        }
    friend demo operator -(demo d)
        {
        demo temp;
        temp.num=-d.num;
        return temp;
        }
    void show(char *s)
        {
        cout<<"num of object "<<s<<" ="<<num<<endl;
        }
    };
int main( )
    {
    demo d1(100), d2;
    d2=-d1;
    d1.show("d1");
    d2.show("d2");
    }

//Write a C++ program to illustrate operator overloading >> and << operator using friend function.
#include<iostream>
using namespace std;
class demo
    {
    int dx, dy;
    public :
    friend void operator <<(ostream &, demo &);
    friend void operator >>(istream &, demo &);
    };
void operator <<(ostream & mycout, demo & d)
    {
    mycout<<"dx = "<<d.dx<<"\t"<<"dy = "<<d.dy<<endl;
    }
void operator >>(istream & mycin, demo & d)
    {
    mycin>>d.dx>>d.dy;
    }
int main( )
    {
    demo d;
    cout<<"Enter the two numbers"<<endl;
    cin>>d;
    cout<<"You entered"<<endl;
    cout<<d;
    }

