//Write a C++ program to enter a number and find the factorial with demonstrating the class and object .
#include<iostream>
using namespace std;
class Fact
{
    int num;
    public :
    void input(int x)
        {
        num = x;
        }
    void getfact( );
};
void Fact :: getfact( )
    {
    long int f = 1;
    short i;
    for(i=1; i<=num; i++)
        f=f*i;
    cout<<"Factorial of"<<num<<" is "<<f<<endl;
    }
int main( )
{
    int n;
    Fact obj;
    cout<<"ENTER THE NUMBER :=";
    cin>>n;
    obj.input(n);
    obj.getfact( );
}

//Write a C++ program to enter a number and find the reverse with demonstrating the class and object .
#include<iostream>
using namespace std;
class Revnum
    {
    int num;
    public :
    void input(int x)
        {
        num =x;
        }
    int getnum( )
        {
        return num;
        }
    long int getrev( );
    };

long int Revnum :: getrev( )
    {
    int save = num, r, rev = 0;
    while (save!=0)
    {
    r = save % 10;
    rev = rev*10 + r;
    save = save/10;
    }
    return rev;
    }
int main( )
    {
    Revnum obj;
    int x;
    cout<<"ENTER THE NUMBER";
    endl(cout);
    cin>>x;
    obj.input(x);
    cout<<"reverse of "<<obj.getnum( ) <<" is "<<obj.getrev( )<<endl;
    }

//Write a C++ program to enter a number and check weather palindrome or not with demonstrating the class and object .
#include<iostream>
using namespace std;
class Palin
    {
    int num;
    public :
    void input(int x)
        {
        num = x;
        }
    int getnum( )
        {
        return num;
        }
    int checkpalin( );
    };
int Palin :: checkpalin( )
    {
    int save = num, r, rev = 0;
    while(save!=0)
        {
        r=save%10;
        rev = rev*10 + r;
        save = save/10;
        }
    if(rev == num)
    return 1;
    else
    return 0;
    }
int main( )
    {
    Palin obj;
    int x;
    cout<<"Enter the number to be checked for palindrome\n";
    cin>>x;
    obj.input(x);
    if(obj.checkpalin( )==1)
        cout<<obj.getnum( )<<"is palindrome\n";
    else
        cout<<obj.getnum( )<<"is not palindrome\n";
    }

