//Write a C ++ program to calculate circumference using defined constant.
#include<iostream>
using namespace std;
#define PI 3.14159
#define NEWLINE '\n'
int main ()
    {
    double r=5.0; // radius
    double circle;
    circle = 2 * PI * r;
    cout << circle;
    cout << NEWLINE;
    return 0;
    }

//Write a C ++ program to define integer, float and character constant and print them.
#include<iostream>
#define val 100
#define floatVal 40.5
#define charVal 'Gt'
using namespace std;
    int main()
    {
    cout<<"Integer Constant:"<<val;
    cout<<"\nFloating point Constant:"<<floatVal;
    cout<<"\nCharacter Constant: \n"<<charVal;
    return 0;
    }

